faqSchema:
{
  "@context": "https://schema.org",
  "@type": "FAQPage",
  "mainEntity": [
    {
      "@type": "Question",
      "name": "What is the price of Ather scooters as a starting point?",
      "acceptedAnswer": {
        "@type": "Answer",
        "text": "The Rizta is ₹1.11 lakh, the 450S is ₹1.29 lakh, and the Apex goes up to ₹1.99 lakh (ex-showroom)."
      }
    },
    {
      "@type": "Question",
      "name": "What is the range of the 450X?",
      "acceptedAnswer": {
        "@type": "Answer",
        "text": "The range is 126 km with a 2.9 kWh battery, whereas the 3.7 kWh battery is 161 km."
      }
    },
    {
      "@type": "Question",
      "name": "How long does it take to fully charge the 450X?",
      "acceptedAnswer": {
        "@type": "Answer",
        "text": "It takes around 4.3–5.5 hours to fully charge and 0-80% charge takes about 3 to 4.5 hours depending on battery kWh."
      }
    },
    {
      "@type": "Question",
      "name": "Is navigation included?",
      "acceptedAnswer": {
        "@type": "Answer",
        "text": "Yes, all models with smart dashboards will include Google Maps navigation, music/call controls and theft alerts."
      }
    },
    {
      "@type": "Question",
      "name": "What is Magic Twist?",
      "acceptedAnswer": {
        "@type": "Answer",
        "text": "A throttle-based regenerative braking that charges the battery while slowing down."
      }
    }
  ]
}

