faqSchema:
{
  "@context": "https://schema.org",
  "@type": "FAQPage",
  "mainEntity": [
    {
      "@type": "Question",
      "name": "What real world range does Bajaj Chetak offer?",
      "acceptedAnswer": {
        "@type": "Answer",
        "text": "In typical city riding conditions, Bajaj Chetak delivers between 110–140 km per charge. Using Eco mode can extend the range further."
      }
    },
    {
      "@type": "Question",
      "name": "How long does Bajaj Chetak take to charge at home?",
      "acceptedAnswer": {
        "@type": "Answer",
        "text": "It takes about 3 hours 25 minutes to charge from 0–80%, and around 5 hours for a full charge using a standard home charger."
      }
    },
    {
      "@type": "Question",
      "name": "What is the boot space of Bajaj Chetak?",
      "acceptedAnswer": {
        "@type": "Answer",
        "text": "Bajaj Chetak offers 35 litres of boot space, the largest in its category, enough to fit a helmet or daily groceries."
      }
    },
    {
      "@type": "Question",
      "name": "How reliable is Bajaj Chetak compared to petrol scooters?",
      "acceptedAnswer": {
        "@type": "Answer",
        "text": "Chetak is highly reliable with its all-metal construction, IP67-rated battery protection, low running cost of about ₹0.15/km, and a class-leading warranty of 3 years or 50,000 km."
      }
    },
    {
      "@type": "Question",
      "name": "How many Bajaj Chetak units were sold in 2025?",
      "acceptedAnswer": {
        "@type": "Answer",
        "text": "In FY2025, Bajaj Chetak sold around 260,000 units, consistently topping monthly sales charts and expanding its market share across India."
      }
    }
  ]
}

